class OrderWriteServiceImplementation implements OrderWriteService{   public function placeOrder($userId, array $items)   {     $order = new Order($userId);     foreach ($items as $item) {       $orderItem = new OrderItem;       // ... populate order item ...       $order->addItem($orderItem);     }     $this->orderRepository->addOrder($order);   }   public function cancelOrder($orderId)   {     $order = $this->orderRepository->findOrderById($orderId);     $order->cancel();   } }