interface LoginCommand {   public function getUsername();   public function getPasswordHash(); } class HttpLoginCommand implements LoginCommand {   private $httpRequest;   public function __construct(HttpRequest $httpRequest)   {     $this->httpRequest = $httpRequest;   }   public function getUsername()   {     return $this->httpRequest->getParameter('username');   }   public function getPasswordHash()   {     return $this->httpRequest->getParameter('passwordHash');   } }